import React from "react";
import "./Skills.css";
import { Link } from "react-router-dom";

function Skills() {
  const skills = [
    {
      title: "HTML",
      description: "Expert in creating structured and semantic HTML.",
      imgSrc:
        "data:image/png;base64,",

      imgStyle: { marginLeft: "130px", width: "100px" },
    },
    {
      title: "CSS",
      description: "Enjoy styling responsive layouts in CSS.",
      imgSrc:
        "data:image/png;base64,",
      imgStyle: { marginLeft: "80px", width: "200px" },
    },
    {
      title: "JavaScript",
      description:
        "Skilled in JavaScript for creating dynamic, interactive websites.",
      imgSrc:
        "data:image/jpeg;base64,",
      imgStyle: { marginLeft: "130px", width: "100px" },
    },
    {
      title: "React",
      description:
        "Proficient in React for building advanced websites with effective code.",
      imgSrc:
        "data:image/png;base64,",
      imgStyle: { marginLeft: "130px", width: "90px" },
    },
    {
      title: "Tailwind CSS",
      description: "Enjoy creating expressive styles using Tailwind CSS.",
      imgSrc:
        "data:image/png;base64,",
      imgStyle: { marginLeft: "80px", width: "200px" },
    },
    {
      title: "Express",
      description:
        "Proficient in Express for building advanced websites with effective code.",
      imgSrc:
        "data:image/png;base64,",
      imgStyle: { marginLeft: "100px", width: "220px" },
    },
  ];

  return (
    <div className="skills-container">
      <h1
        className="skills-title slide-in-left underline ml-[-1000px]" // Apply the slide-in-left animation class
      >
        My Skills
      </h1>
      <div className="skills-grid">
        {skills.map((skill, index) => (
          <div key={index} className="skill-card">
            <img
              src={skill.imgSrc}
              alt={skill.title}
              className="skill-icon"
              style={skill.imgStyle}
            />
            <h2 className="skill-title">{skill.title}</h2>
            <p className="skill-description">{skill.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Skills;
